function PbTableCtrl($scope, $http, $location, $log, $window, localStorageService) {

   $scope.nSelect = 0;  // initialize our variable to null


  this.isArray = Array.isArray;

  this.isClickable = function () {
    return $scope.properties.isBound('selectedRow');
  };

    var respuestaPrimerAPISuccess = false;
    var respuestaSegundaAPISuccess = false;
    var identificacionCompleta = "";

  this.selectRow = function (row,index) {
    $scope.properties.cambioSeleccion=true;

    if (this.isClickable()) {

      $scope.nSelect = index;
      $scope.properties.selectedRow = row;
      var cantidad= row.identificacion.length;
      var cantCeros="000000000000000".substring(0,15-cantidad);
      identificacionCompleta=row.idTipoIdentificacion+cantCeros+row.identificacion+row.dv;
      $scope.properties.idCompleto=identificacionCompleta;
      $scope.properties.cambio=true;

      urlListador=$scope.properties.entorno+"businesses/v0/businesses/"+identificacionCompleta+"/economic-data/financial-statements/";

        $http.get(urlListador).then(function(response) {
            $scope.properties.respuestaAPISuccess= {data:response.data,error:false,textoError:"",estado:response.status,documento:row.identificacion};
            peticionDetalleInforme(identificacionCompleta);
            $scope.properties.cambioListador=true;

        }, function (response) {
            var error="";
            $scope.properties.cambioListador=true;
            if(response.status===500){
                textEr = texToaster(response.status,"");
                $scope.properties.respuestaAPISuccess= {data:"",error:true,textoError:textEr,estado:response.status,documento:row.identificacion};
            }else{
                $scope.properties.respuestaAPISuccess= {data:"",error:true,textoError:"",estado:response.status,documento:row.identificacion};
            }
        });

    }
  };

  function peticionDetalleInforme(identificacionCompleta){
    var qualityBlock=$scope.properties.entorno+"ratings/v1/qualitative-blocks/"+$scope.properties.respuestaAPISuccess.data.data[0].id;
    var urlDetalleInforme=$scope.properties.entorno+"businesses/v0/businesses/"+identificacionCompleta+"/economic-data/financial-statements/"+$scope.properties.respuestaAPISuccess.data.data[0].id;
    $http.get(urlDetalleInforme).then(function(respuestaDetalleInforme){
        $scope.properties.respuestaAPIDetalleInfSuccess=[];
        $scope.properties.respuestaAPIQB=[];
        $scope.properties.respuestaAPIDetalleInfSuccess.push({data:respuestaDetalleInforme.data,error:false,textoError:"",estado:respuestaDetalleInforme.status,documento:identificacionCompleta});


        //$scope.properties.cambioSeleccionInforme=true;
        $scope.properties.selectedRow2 = $scope.properties.respuestaAPISuccess.data.data[0];
        $http.get(qualityBlock).then(function(response) {
            $scope.properties.respuestaAPIQB.push({data:response.data,error:false,textoError:"",estado:response.status,documento:$scope.properties.idCompletoBusiness});
            $scope.properties.cambioSeleccioTablaEEFF=true;

        }, function (response) {
            $scope.properties.respuestaAPIQB.push({data:"",error:true,textoError:"",estado:response.status,documento:$scope.properties.idCompletoBusiness});
            $scope.properties.cambioSeleccioTablaEEFF=true;
        });
    },function(respuestaDetalleInforme){
        var textEr="";
        $scope.properties.cambioSeleccioTablaEEFF=true;
        if(respuestaDetalleInforme.status===400){
            textEr = texToaster(respuestaDetalleInforme.status,"");
            $scope.properties.respuestaAPIDetalleInfSuccess={data:"",error:true,textoError:textEr,estado:respuestaDetalleInforme.status};
        }else if(respuestaDetalleInforme.status===401){
            textEr = texToaster(respuestaDetalleInforme.status,"");
            $scope.properties.respuestaAPIDetalleInfSuccess={data:"",error:true,textoError:textEr,estado:respuestaDetalleInforme.status};
        }else if(respuestaDetalleInforme.status===403){
            textEr = texToaster(respuestaDetalleInforme.status,"");
            $scope.properties.respuestaAPIDetalleInfSuccess={data:"",error:true,textoError:textEr,estado:respuestaDetalleInforme.status};
        }else if(respuestaDetalleInforme.status===404){
            textEr = texToaster(respuestaDetalleInforme.status,"");
            $scope.properties.respuestaAPIDetalleInfSuccess={data:"",error:true,textoError:textEr,estado:respuestaDetalleInforme.status};
        }else if(respuestaDetalleInforme.status===409){
            textEr = texToaster(respuestaDetalleInforme.status,"");
            $scope.properties.respuestaAPIDetalleInfSuccess={data:"",error:true,textoError:textEr,estado:respuestaDetalleInforme.status};
        }else if(respuestaDetalleInforme.status===500){
            textEr = texToaster(respuestaDetalleInforme.status,"");
            $scope.properties.respuestaAPIDetalleInfSuccess={data:"",error:true,textoError:textEr,estado:respuestaDetalleInforme.status};
        }
        $scope.properties.cambioSeleccioTablaEEFF=true;
    });
  }


  function texToaster(statusError,textoError){
    var rtn = [];
    rtn.push({
                    "text": "Error t√©cnico, contacte al Administrador. "+textoError,
                    "title":"Respuesta del servidor",
                    "type":"info"
                });
    return rtn;
  }

  this.isSelected = function(row) {
    return angular.equals(row, $scope.properties.selectedRow);
  };



}
